```typescript
const DestinationMultiSelect: React.FC = () => {
  const { 
    selectedDestinations, 
    addDestination, 
    removeDestination, 
    isDestinationSelected,
    destinations 
  } = useDestinations();

  const handleToggleDestination = (destination: Destination) => {
    if (isDestinationSelected(destination.id)) {
      removeDestination(destination.id);
    } else if (selectedDestinations.length < 4) {
      addDestination(destination);
    }
  };

  return (
    <div className="destination-multi-select">
      <h3>Combine destinations (max 4)</h3>
      <div className="destination-options">
        {destinations.map((destination) => (
          <label key={destination.id} className="flex items-center space-x-2">
            <input
              type="checkbox"
              checked={isDestinationSelected(destination.id)}
              onChange={() => handleToggleDestination(destination)}
              disabled={!isDestinationSelected(destination.id) && selectedDestinations.length >= 4}
            />
            <span>{destination.name}</span>
          </label>
        ))}
      </div>
    </div>
  );
};
```

