// Transition component for content changes
const ContentTransition: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  return (
    <div className="transition-opacity duration-300 ease-in-out">
      {children}
    </div>
  );
};

// Animation hook for fade effects
const useFadeAnimation = () => {
  const [isVisible, setIsVisible] = useState(false);
  const nodeRef = useRef(null);

  useEffect(() => {
    setIsVisible(true);
    return () => setIsVisible(false);
  }, []);

  return {
    nodeRef,
    fadeProps: {
      classNames: 'fade',
      timeout: 300,
      in: isVisible,
      unmountOnExit: true
    }
  };
};

// Animated destination change
const AnimatedDestinationContent: React.FC = () => {
  const { primaryDestination } = useDestinations();
  const [prevDestination, setPrevDestination] = useState(primaryDestination);
  const [isTransitioning, setIsTransitioning] = useState(false);

  useEffect(() => {
    if (primaryDestination?.id !== prevDestination?.id) {
      setIsTransitioning(true);
      const timer = setTimeout(() => {
        setPrevDestination(primaryDestination);
        setIsTransitioning(false);
      }, 300);
      return () => clearTimeout(timer);
    }
  }, [primaryDestination, prevDestination]);

  return (
    <div className={`transition-all duration-300 ${isTransitioning ? 'opacity-0' : 'opacity-100'}`}>
      {/* Content based on prevDestination */}
      <h2>{prevDestination?.name}</h2>
      <p>{prevDestination?.description}</p>
    </div>
  );
};
```

