const AdaptiveImageGrid: React.FC = () => {
  const { selectedDestinations } = useDestinations();

  // Determine grid layout based on number of destinations
  const getGridLayout = () => {
    switch(selectedDestinations.length) {
      case 1:
        return 'grid-cols-1';
      case 2:
        return 'grid-cols-1 md:grid-cols-2';
      case 3:
        return 'grid-cols-1 md:grid-cols-3';
      case 4:
