// useDestinations hook
const useDestinations = () => {
  const context = useContext(DestinationContext);
  if (!context) {
    throw new Error('useDestinations must be used within a DestinationProvider');
  }
  return context;
};

// useDestinationNavigation hook
const useDestinationNavigation = () => {
  const { setPrimaryDestination, destinations } = useDestinations();

  const navigateToDestination = useCallback((destinationId: string) => {
    const destination = destinations.find(d => d.id === destinationId);
    if (destination) {
      setPrimaryDestination(destination);
      // Could also handle URL updates here if using a router
    }
  }, [destinations, setPrimaryDestination]);

  return { navigateToDestination };
};

// useDestinationImages hook
const useDestinationImages = () => {
  const { selectedDestinations } = useDestinations();

  const getOptimizedImageUrl = useCallback((destination: Destination, size: 'small' | 'medium' | 'large') => {
    const sizeMap = {
      small: '400w',
      medium: '800w',
      large: '1200w'
    };
    return `${destination.image.replace('.jpg', `-${sizeMap[size]}.jpg`)}`;
  }, []);

  return { getOptimizedImageUrl };
};
```

