```typescript
// Accessible multi-select component
const AccessibleMultiSelect: React.FC = () => {
  const { selectedDestinations, addDestination, removeDestination, isDestinationSelected, destinations } = useDestinations();
  const [isExpanded, setIsExpanded] = useState(false);

  const toggleExpanded = () => setIsExpanded(!isExpanded);

  const handleKeyDown = (e: React.KeyboardEvent, destination: Destination) => {
    if (e.key === 'Enter' || e.key === ' ') {
      e.preventDefault();
      handleToggleDestination(destination);
    }
  };

  const handleToggleDestination = (destination: Destination) => {
    if (isDestinationSelected(destination.id)) {
      removeDestination(destination.id);
    } else if (selectedDestinations.length < 4) {
      addDestination(destination);
    }
  };

  return (
    <div className="accessible-multi-select">
      <button
        aria-expanded={isExpanded}
        aria-controls="destination-options"
        onClick={toggleExpanded}
        className="select-button"
      >
        Select destinations ({selectedDestinations.length}/4)
      </button>

      <div 
        id="destination-options"
        className={`options-container ${isExpanded ? 'visible' : 'hidden'}`}
        role="listbox"
        aria-multiselectable="true"
      >
        {destinations.map((destination) => {
          const isSelected = isDestinationSelected(destination.id);
          const isDisabled = !isSelected && selectedDestinations.length >= 4;

          return (
            <div 
              key={destination.id}
              role="option"
              aria-selected={isSelected}
              tabIndex={isDisabled ? -1 : 0}
              onClick={() => !isDisabled && handleToggleDestination(destination)}
              onKeyDown={(e) => !isDisabled && handleKeyDown(e, destination)}
              className={`option ${isSelected ? 'selected' : ''} ${isDisabled ? 'disabled' : ''}`}
              aria-disabled={isDisabled}
            >
              <span className="visually-hidden">{isSelected ? 'Selected' : 'Not selected'}</span>
              {destination.name}
            </div>
          );
        })}
      </div>
    </div>
  );
};
```

