// Analytics service
const AnalyticsService = {
  trackPageView: (page: string) => {
    // Implementation would depend on analytics provider
    console.log(`Page view: ${page}`);
    // window.gtag('config', 'GA-ID', { page_path: page });
  },

  trackEvent: (category: string, action: string, label?: string, value?: number) => {
    console.log(`Event: ${category} - ${action} - ${label} - ${value}`);
    // window.gtag('event', action, { event_category: category, event_label: label, value });
  },

  trackDestinationSelection: (destinationId: string, isMultiSelect: boolean) => {
    AnalyticsService.trackEvent(
      'Destination', 
      isMultiSelect ? 'AddToMultiSelect' : 'PrimarySelection', 
      destinationId
    );
  },

  trackPerformance: (metric: string, value: number) => {
    AnalyticsService.trackEvent('Performance', metric, undefined, value);
  }
};

// Performance monitoring hook
const usePerformanceMonitoring = () => {
  useEffect(() => {
    // Track page load time
    if (window.performance) {
      const pageLoadTime = window.performance.timing.domContentLoadedEventEnd - 
                          window.performance.timing.navigationStart;
      AnalyticsService.trackPerformance('pageLoadTime', pageLoadTime);
    }

    // Set up performance observer for metrics
    if ('PerformanceObserver' in window) {
      const observer = new PerformanceObserver((list) => {
        for (const entry of list.getEntries()) {
          AnalyticsService.trackPerformance(entry.name, entry.duration);
        }
      });
      observer.observe({ entryTypes: ['measure'] });
      return () => observer.disconnect();
    }
  }, []);

  // Function to measure component performance
  const measurePerformance = useCallback((name: string, callback: () => void) => {
    performance.mark(`${name}-start`);
    callback();
    performance.mark(`${name}-end`);
    performance.measure(name, `${name}-start`, `${name}-end`);
  }, []);

  return { measurePerformance };
};
```

