```typescript
// DestinationStateContext.tsx
const DestinationStateContext = React.createContext<DestinationState | null>(null);

export const DestinationStateProvider: React.FC = ({ children }) => {
  const [state, setState] = useState<DestinationState>(initialState);
  const value = useMemo(() => state, [state]);
  return (
    <DestinationStateContext.Provider value={value}>
      {children}
    </DestinationStateContext.Provider>
  );
};

// DestinationActionsContext.tsx
const DestinationActionsContext = React.createContext<DestinationActions | null>(null);

export const DestinationActionsProvider: React.FC = ({ children }) => {
  const actions = useMemo(() => ({
    setPrimaryDestination: (destination: Destination) => { /* implementation */ },
    addDestination: (destination: Destination) => { /* implementation */ },
    // ... other actions
  }), []);

  return (
    <DestinationActionsContext.Provider value={actions}>
      {children}
    </DestinationActionsContext.Provider>
  );
};

// Selector hooks
export const useDestinationState = () => {
  const context = useContext(DestinationStateContext);
  if (context === null) {
    throw new Error('useDestinationState must be used within a DestinationStateProvider');
  }
  return context;
};

export const useDestinationActions = () => {
  const context = useContext(DestinationActionsContext);
  if (context === null) {
    throw new Error('useDestinationActions must be used within a DestinationActionsProvider');
  }
  return context;
};

// Usage in a component
