```typescript
const AdaptiveImageGrid: React.FC = () => {
  const { selectedDestinations } = useDestinations();

  // Determine grid layout based on number of destinations
  const getGridLayout = () => {
    switch(selectedDestinations.length) {
      case 1:
        return 'grid-cols-1';
      case 2:
        return 'grid-cols-1 md:grid-cols-2';
      case 3:
        return 'grid-cols-1 md:grid-cols-3';
      case 4:
        return 'grid-cols-2 md:grid-cols-4';
      default:
        return 'grid-cols-1';
    }
  };

  return (
    <div className={`grid ${getGridLayout()} gap-4 transition-all duration-300`}>
      {selectedDestinations.map((destination) => (
        <div key={destination.id} className="overflow-hidden rounded-lg aspect-video">
          <img
            src={destination.image}
            srcSet={`${destination.image} 1x, ${destination.image.replace('.jpg', '@2x.jpg')} 2x`}
            alt={destination.name}
            loading="lazy"
            className="w-full h-full object-cover transition-transform duration-500 hover:scale-105"
          />
        </div>
      ))}
    </div>
  );
};
```

